import express, { Request, Response } from 'express';
import { AppDataSource } from './data_source';
import { Product } from './entity/Product';

const app = express();
app.use(express.json());

// Veritabanını başlat
AppDataSource.initialize()
  .then(() => {
    console.log('Veritabanına başarıyla bağlanıldı.');
  })
  .catch((error) => console.log('Veritabanı bağlantı hatası:', error));

// Ürün ekleme (CREATE)
app.post('/product', async (req: Request, res: Response) => {
  const productRepository = AppDataSource.getRepository(Product);
  const newProduct = productRepository.create(req.body);
  await productRepository.save(newProduct);
  res.status(201).json(newProduct);
});

// Tüm ürünleri listeleme (READ)
app.get('/product', async (req: Request, res: Response) => {
  console.log(response.data); 
  const productRepository = AppDataSource.getRepository(Product);
  const products = await productRepository.find();
  res.json(products);
});

// Belirli bir ürünü listeleme (READ)
app.get('/product/:id', async (req: Request, res: Response) => {
  const productRepository = AppDataSource.getRepository(Product);
  const product = await productRepository.findOneBy({ id: parseInt(req.params.id) });
  if (product) {
    res.json(product);
  } else {
    res.status(404).json({ message: 'Ürün bulunamadı' });
  }
});

// Ürün güncelleme (UPDATE)
app.put('/product/:id', async (req: Request, res: Response) => {
  const productRepository = AppDataSource.getRepository(Product);
  await productRepository.update(req.params.id, req.body);
  const updatedProduct = await productRepository.findOneBy({ id: parseInt(req.params.id) });
  if (updatedProduct) {
    res.json(updatedProduct);
  } else {
    res.status(404).json({ message: 'Ürün güncellenemedi' });
  }
});

// Ürün silme (DELETE)
app.delete('/product/:id', async (req: Request, res: Response) => {
  const productRepository = AppDataSource.getRepository(Product);
  const deleteResult = await productRepository.delete(req.params.id);
  if (deleteResult.affected) {
    res.json({ message: 'Ürün silindi' });
  } else {
    res.status(404).json({ message: 'Ürün bulunamadı' });
  }
});

// Sunucuyu başlat
const PORT = 3000;
app.listen(PORT, () => {
  console.log(`Sunucu ${PORT} portunda çalışıyor.`);
});
